svg.script:

function animate() {
  var stem = document.getElementById("stem");
  var cap = document.getElementById("cap");

  // Frame 1
  stem.setAttribute("height", "50");
  cap.setAttribute("r", "40");

  // Frame 2
  setTimeout(function() {
    stem.setAttribute("height", "75");
    cap.setAttribute("r", "50");
  }, 100);

  // Frame 3
  setTimeout(function() {
    stem.setAttribute("height", "100");
    cap.setAttribute("r", "60");
  }, 200);

  // Frame 4
  setTimeout(function() {
    stem.setAttribute("height", "125");
    cap.setAttribute("r", "70");
  }, 300);

  // Frame 5
  setTimeout(function() {
    stem.setAttribute("height", "150");
    cap.setAttribute("r", "80");
  }, 400);

  // Frame 6
  setTimeout(function() {
    stem.setAttribute("height", "175");
    cap.setAttribute("r", "90");
  }, 500);

  // Frame 7
  setTimeout(function() {
    stem.setAttribute("height", "200");
    cap.setAttribute("r", "100");
  }, 600);

  // Frame 8
  setTimeout(function() {
    stem.setAttribute("height", "225");
    cap.setAttribute("r", "110");
  }, 700);

  // Frame 9
  setTimeout(function() {
    stem.setAttribute("height", "250");
    cap.setAttribute("r", "120");
  }, 800);

  // Frame 10
  setTimeout(function() {
    stem.setAttribute("height", "275");
    cap.setAttribute("r", "130");
  }, 900);

  // Add more frames as needed
}
